<input bind:this={input} bind:value={value} class="{_class} dark:bg-gray-700 dark:border-gray-900 dark:focus:border-blue-600 form-input block" placeholder="{placeholder}">

<script>
    export let placeholder
    export let value = ''
    export let type = 'text'
    import { onMount } from 'svelte'
    
    let input

    let _class
    export { _class as class }

    export function changeType(type) {
    	input.type = type
    }

    export function resetType() {
    	input.type = type
    }

    onMount(() => changeType(type))
</script>